@import url("https://fonts.googleapis.com/css2?family=Rubik:wght@400&display=swap");
.home-widget-root {
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
  height: 220px;
  min-height: 180px;
  width: 100%;
  background: #0b1e32;
  border-radius: 0;
  overflow: hidden;
  box-sizing: border-box;
}

.home-widget-overlay {
  position: absolute;
  top: 0;
  left: 0;
  height: 100%;
  width: 100%;
  background: none;
  z-index: 1;
  border-radius: 0;
  pointer-events: none;
}

.home-widget-overlay::before {
  content: "";
  display: block;
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-image: url("https://consulai.com/wp-content/uploads/2026/01/5.png");
  background-repeat: no-repeat;
  background-position: center top;
  background-size: 100% 100%;
  opacity: 1;
  z-index: 1;
  border-radius: 0;
}

.home-widget-content {
  position: relative;
  z-index: 2;
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
}

.home-title {
  font-family: "Rubik", sans-serif;
  color: #fff;
  font-size: 0.85rem;
  letter-spacing: 0.35em;
  margin-bottom: 0.5rem;
  font-weight: 400;
}

.home-subtitle {
  color: #fff;
  font-size: 1.9rem;
  font-family: "Rubik", sans-serif;
  text-align: center;
  font-weight: 400;
}

